// Break and Work Controllers
import React, {Component} from 'react';

export default class Settings extends Component {
    handleWorkIncrement = () => {
        this.props.onIncreaseSession()
        if (this.props.timerRunning === false) {
            this.props.setCurrentTime(this.props.workTime)
        }
    }

    handleWorkDecrement = () => {
        this.props.onDecreaseSession()
        if (this.props.timerRunning === false) {
            this.props.setCurrentTime(this.props.workTime)
        }
    }

    render() {

        const btnClassName = this.props.isStart ? 'disable': '';

        return(
            <div className="settings">
                <div className="settings-section">
                  <label id="break-label">Break Length</label>  
                  <div>
                      <button className={btnClassName} id="break-increment" onOnClick={this.props.onIncreaseBreak}>+</button>
                      <span id="break-length">{this.props.breakLength}</span>
                      <button className={btnClassName} id="break-increment" onClick={this.props.onDecreaseBreak}>-</button>
                  </div>
                </div>
                <div className="settings-section">
                    <label id="session-label">Session Length</label>
                    <button className={btnClassName} id="session-increment" onClick={this.props.onIncreaseSession}>+</button>
                    <span id="session-length">{this.props.sessionLength}</span>
                    <button className={btnClassName} id="session-decrement" onClick={this.props.onDecreaseSession}></button>
                </div>
            </div>
        )
    }
}
