import React from 'react';

const Break = props => {
    return (
        <div className="break-container">
            <h2 id="break-label">Break Length</h2>

            <div className="break-btn">
                <button id="break-increment" onClick={props.incrementBreakLength}>
                    <i className="fa fa-plus-square fa-2x"/>
                </button>

                <h3 id="break-length">
                    {props.breakLengthSeconds}
                </h3>

                <button id="break-decrement" onClick={props.decrementBreakLength}>
                    <i className="fa fa-minus-square fa-2x"/>
                </button>
            </div>
        </div>
    )
}
